// src/pages/MyListings.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MyListings = ({ user }) => {
    const [listings, setListings] = useState([]);
    const [form, setForm] = useState({
        name: '',
        price: '',
        description: ''
    });

    const fetchListings = async () => {
        const res = await axios.get(`http://localhost:5000/api/trade-listings/user/${user.username}`);
        setListings(res.data);
    };

    useEffect(() => {
        fetchListings();
    }, []);

    const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/trade-listings', { ...form, offeredBy: user.username });
            setForm({ name: '', price: '', description: '' });
            fetchListings();
        } catch(err) {
            console.log(err);
        }
    };

    return (
        <div>
            <h2 className="text-xl mb-4">My Trade Listings</h2>
            <form onSubmit={handleSubmit} className="mb-6">
                <div className="flex flex-col space-y-2">
                    <input type="text" name="name" placeholder="Listing Name" value={form.name} onChange={handleChange} required className="p-2 border rounded" />
                    <input type="number" name="price" placeholder="Price (ETH)" value={form.price} onChange={handleChange} required className="p-2 border rounded" />
                    <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} className="p-2 border rounded"></textarea>
                    <button type="submit" className="bg-green-500 text-white p-2 rounded">Add Listing</button>
                </div>
            </form>
            <div>
                <h3 className="text-lg mb-2">Your Listings:</h3>
                {listings.length === 0 ? <p>No listings added yet.</p> :
                    <ul className="space-y-2">
                        {listings.map(listing => (
                            <li key={listing._id} className="p-2 border rounded">
                                <p><strong>Name:</strong> {listing.name}</p>
                                <p><strong>Price:</strong> {listing.price} ETH</p>
                                <p><strong>Description:</strong> {listing.description}</p>
                            </li>
                        ))}
                    </ul>
                }
            </div>
        </div>
    );
};

export default MyListings;
