// frontend/src/contexts/SocketContext.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import io from 'socket.io-client';
import axios from 'axios';

const SocketContext = createContext();

export const useSocket = () => {
    return useContext(SocketContext);
};

export const SocketProvider = ({ children }) => {
    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState({});
    const [tradeRequests, setTradeRequests] = useState([]);

    useEffect(() => {
        const newSocket = io('http://localhost:5000', {
            transports: ['websocket', 'polling'],
        });
        setSocket(newSocket);

        return () => newSocket.close();
    }, []);

    useEffect(() => {
        if (!socket) return;

        const username = localStorage.getItem('username');
        if (username) {
            socket.emit('userOnline', username);
        }

        socket.on('updateUserStatus', (users) => {
            setOnlineUsers(users);
        });

        socket.on('receiveTradeRequest', (data) => {
            setTradeRequests((prev) => [...prev, data]);
        });

        socket.on('tradeAccepted', (data) => {
            // Optionally handle real-time trade acceptance notifications
            // For example, you can fetch the updated trade history here
        });

        return () => {
            socket.off('updateUserStatus');
            socket.off('receiveTradeRequest');
            socket.off('tradeAccepted');
        };
    }, [socket]);

    const value = {
        socket,
        onlineUsers,
        tradeRequests,
        setTradeRequests,
    };

    return <SocketContext.Provider value={value}>{children}</SocketContext.Provider>;
};
