// src/pages/TradeHistory.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TradeHistory = ({ user }) => {
    const [trades, setTrades] = useState([]);

    const fetchTrades = async () => {
        const res = await axios.get(`http://localhost:5000/api/trades/history/${user.username}`);
        setTrades(res.data);
    };

    useEffect(() => {
        fetchTrades();
    }, []);

    return (
        <div>
            <h2 className="text-xl mb-4">Trade History</h2>
            {trades.length === 0 ? <p>No trades yet.</p> :
                <ul className="space-y-2">
                    {trades.map(trade => (
                        <li key={trade._id} className="p-2 border rounded">
                            <p><strong>Offered By:</strong> {trade.offeredBy}</p>
                            <p><strong>Accepted By:</strong> {trade.acceptedBy}</p>
                            <p><strong>Product Offered:</strong> {trade.productOffered}</p>
                            <p><strong>Product Gotten:</strong> {trade.productGotten}</p>
                            <p><strong>Date:</strong> {new Date(trade.dateCreated).toLocaleString()}</p>
                        </li>
                    ))}
                </ul>
            }
        </div>
    );
};

export default TradeHistory;
