// src/pages/TradeInterface.js
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../App';

const TradeInterface = () => {
  const { user, socket, onlineUsers } = useContext(UserContext);
  const [listings, setListings] = useState([]);
  const [products, setProducts] = useState([]);
  const [selectedListing, setSelectedListing] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState('');

  const fetchListings = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/trade-listings');
      // Exclude user's own listings
      const otherListings = res.data.filter((l) => l.offeredBy !== user.username);
      setListings(otherListings);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      const userProducts = res.data.filter((p) => p.offeredBy === user.username);
      setProducts(userProducts);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchListings();
    fetchProducts();
  }, []);

  const handleTradeClick = (listing) => {
    setSelectedListing(listing);
  };

  const handleTradeRequest = async () => {
    if (!selectedProduct) {
      alert('Please select a product to offer');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/trades', {
        offeredBy: user.username,
        acceptedBy: selectedListing.offeredBy,
        productOffered: selectedProduct,
        productGotten: selectedListing.product,
      });

      // Notify the other user via Socket.io if online
      if (onlineUsers[selectedListing.offeredBy]) {
        socket.emit('tradeRequest', {
          to: selectedListing.offeredBy,
          from: user.username,
          listing: selectedListing,
        });
      }

      alert('Trade request sent');
      setSelectedListing(null);
      fetchListings();
      fetchProducts();
    } catch (error) {
      alert(error.response.data.message || 'Error sending trade request');
    }
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Trade Interface</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {listings.map((listing) => {
          const isOnline = onlineUsers[listing.offeredBy];
          return (
            <div
              key={listing._id}
              className={`p-4 border rounded cursor-pointer ${
                isOnline ? 'bg-green-100' : 'bg-gray-100'
              }`}
              onClick={() => handleTradeClick(listing)}
            >
              <h3 className="font-bold">{listing.product}</h3>
              <p>Price: ${listing.price}</p>
              <p>Shipping: {listing.shippingOptions}</p>
              <p>{listing.description}</p>
              <p className="text-sm text-gray-600">Offered by: {listing.offeredBy}</p>
              <span
                className={`inline-block mt-2 px-2 py-1 text-xs rounded ${
                  isOnline ? 'bg-green-500 text-white' : 'bg-gray-500 text-white'
                }`}
              >
                {isOnline ? 'Online' : 'Offline'}
              </span>
            </div>
          );
        })}
      </div>

      {selectedListing && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded w-full max-w-md">
            <h3 className="text-lg font-bold mb-4">Trade with {selectedListing.offeredBy}</h3>
            <p>Product Wanted: {selectedListing.product}</p>
            <div className="mt-4">
              <label className="block mb-2">Select Your Product to Offer:</label>
              <select
                value={selectedProduct}
                onChange={(e) => setSelectedProduct(e.target.value)}
                className="w-full px-3 py-2 border rounded"
              >
                <option value="">Select Product</option>
                {products.map((p) => (
                  <option key={p._id} value={p.name}>
                    {p.name}
                  </option>
                ))}
              </select>
            </div>
            <div className="mt-4 flex justify-end space-x-2">
              <button
                onClick={() => setSelectedListing(null)}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={handleTradeRequest}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Send Trade Request
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TradeInterface;
