// src/components/Navbar.js
import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserContext } from '../App';

const Navbar = () => {
  const { user, setUser, onlineUsers } = useContext(UserContext);
  const navigate = useNavigate();
  const [tradeRequests, setTradeRequests] = useState([]);

  useEffect(() => {
    // Placeholder for trade requests
    // In a real application, you would handle this via Socket.io events
  }, []);

  const handleLogout = () => {
    setUser(null);
    navigate('/login');
  };

  return (
    <nav className="bg-blue-500 text-white p-4 flex justify-between items-center">
      <div>
        <span className="font-bold">Welcome, {user.username}</span>
      </div>
      <div className="flex space-x-4">
        <Link to="my-products" className="hover:underline">
          My Products
        </Link>
        <Link to="my-listings" className="hover:underline">
          My Listings
        </Link>
        <Link to="trade-interface" className="hover:underline">
          Trade Interface
        </Link>
        <Link to="trade-history" className="hover:underline">
          Trade History
        </Link>
        <Link to="change-password" className="hover:underline">
          Profile
        </Link>
        <button onClick={handleLogout} className="hover:underline">
          Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
