// frontend/src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

function Dashboard() {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [onlineUsers, setOnlineUsers] = useState({});
  const [tradeRequests, setTradeRequests] = useState([]);

  useEffect(() => {
    if (!username) {
      navigate('/');
    } else {
      socket.emit('userOnline', username);
    }

    socket.on('updateUserStatus', (users) => {
      setOnlineUsers(users);
    });

    socket.on('receiveTradeRequest', (data) => {
      setTradeRequests((prev) => [...prev, data]);
    });

    return () => {
      socket.disconnect();
    };
  }, [username, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('username');
    navigate('/');
  };

  const handleTradeRequestClick = async (request) => {
    if (window.confirm(`Trade Request from ${request.sender}: ${request.description}`)) {
        // Fetch the listing details
        const listingRes = await axios.get(`http://localhost:5000/api/trade-listings/${request.listingId}`);
        const listing = listingRes.data;

        // Create Trade
        await axios.post('http://localhost:5000/api/trades', {
            offerBy: request.sender,
            acceptedBy: username,
            productOffered: request.offeredProduct,
            productGotten: listing.product
        });

        // Emit a socket event to update both users
        socket.emit('tradeAccepted', { 
            offerBy: request.sender, 
            acceptedBy: username,
            productOffered: request.offeredProduct,
            productGotten: listing.product
        });

        alert('Trade accepted!');
        setTradeRequests(prev => prev.filter(req => req !== request));
    }
};

  return (
    <div className="flex flex-col h-screen">
      {/* Navbar */}
      <nav className="flex justify-between items-center p-4 bg-blue-500 text-white">
        <div>Welcome, {username}</div>
        <div className="flex items-center space-x-4">
          <button onClick={() => navigate('/dashboard/change-password')} className="hover:underline">Profile</button>
          <button onClick={handleLogout} className="hover:underline">Logout</button>
          {tradeRequests.length > 0 && (
            <div className="relative">
              <span className="bg-red-500 rounded-full px-2 py-1 text-xs">{tradeRequests.length}</span>
              <button 
                onClick={() => {
                  tradeRequests.forEach(req => handleTradeRequestClick(req));
                  setTradeRequests([]);
                }}
                className="ml-2"
              >
                Trade Request
              </button>
            </div>
          )}
        </div>
      </nav>

      {/* Body */}
      <div className="flex flex-1">
        {/* Sidebar */}
        <aside className="w-1/5 bg-gray-200 p-4">
          <button 
            className="w-full mb-2 px-4 py-2 bg-green-500 text-white rounded"
            onClick={() => navigate('/dashboard/my-products')}
          >
            My Products
          </button>
          <button 
            className="w-full mb-2 px-4 py-2 bg-green-500 text-white rounded"
            onClick={() => navigate('/dashboard/my-listings')}
          >
            My Listings
          </button>
          <button 
            className="w-full mb-2 px-4 py-2 bg-green-500 text-white rounded"
            onClick={() => navigate('/dashboard/trade-interface')}
          >
            Trade Interface
          </button>
          <button 
            className="w-full px-4 py-2 bg-green-500 text-white rounded"
            onClick={() => navigate('/dashboard/trade-history')}
          >
            Trade History
          </button>
        </aside>

        {/* Main Content */}
        <main className="flex-1 p-4 overflow-auto">
          <Outlet context={{ socket, onlineUsers }} />
        </main>
      </div>
    </div>
  );
}

export default Dashboard;
