// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import MyProducts from './MyProducts';
import MyListings from './MyListings';
import TradeInterface from './TradeInterface';
import TradeHistory from './TradeHistory';
import socket from '../socket'; // Import the shared socket instance
import axios from 'axios';

const Dashboard = ({ user, setUser }) => {
    const navigate = useNavigate();
    const [onlineUsers, setOnlineUsers] = useState({});
    const [tradeRequests, setTradeRequests] = useState(0); // For simplicity
    const [pendingTrades, setPendingTrades] = useState([]); // To handle multiple trade requests

    useEffect(() => {
        // Emit user online status
        socket.emit('user_online', user.username);

        // Listen for user status updates
        socket.on('update_user_status', (users) => {
            setOnlineUsers(users);
        });

        // Listen for trade requests
        socket.on('trade_request', (data) => {
            if (data.acceptedBy === user.username) {
                setTradeRequests(prev => prev + 1);
                setPendingTrades(prev => [...prev, data]);
            }
        });

        return () => {
            socket.disconnect();
        };
    }, [user.username]);

    const handleLogout = () => {
        setUser(null);
        navigate('/');
    };

    return (
        <div className="flex flex-col h-screen">
            <Navbar username={user.username} onLogout={handleLogout} tradeRequests={tradeRequests} />
            <div className="flex flex-1">
                <aside className="w-1/5 bg-gray-200 p-4">
                    <nav className="flex flex-col space-y-2">
                        <Link to="my-products" className="p-2 bg-blue-500 text-white rounded">My Products</Link>
                        <Link to="my-listings" className="p-2 bg-blue-500 text-white rounded">My Listings</Link>
                        <Link to="trade-interface" className="p-2 bg-blue-500 text-white rounded">Trade Interface</Link>
                        <Link to="trade-history" className="p-2 bg-blue-500 text-white rounded">Trade History</Link>
                    </nav>
                </aside>
                <main className="flex-1 p-4 overflow-auto">
                    <Routes>
                        <Route path="my-products" element={<MyProducts user={user} />} />
                        <Route path="my-listings" element={<MyListings user={user} />} />
                        <Route path="trade-interface" element={<TradeInterface user={user} onlineUsers={onlineUsers} />} />
                        <Route path="trade-history" element={<TradeHistory user={user} />} />
                        <Route path="*" element={<div>Select an option from the sidebar.</div>} />
                    </Routes>
                </main>
            </div>
        </div>
    );
};

export default Dashboard;
