// src/pages/TradeHistory.js
import React, { useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { UserContext } from '../App';

const TradeHistory = () => {
  const { user } = useContext(UserContext);
  const [trades, setTrades] = useState([]);

  const fetchTrades = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/trades');
      const userTrades = res.data.filter(
        (t) => t.offeredBy === user.username || t.acceptedBy === user.username
      );
      setTrades(userTrades);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchTrades();
  }, []);

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Trade History</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white">
          <thead>
            <tr>
              <th className="py-2">Offered By</th>
              <th className="py-2">Accepted By</th>
              <th className="py-2">Product Offered</th>
              <th className="py-2">Product Gotten</th>
              <th className="py-2">Date</th>
            </tr>
          </thead>
          <tbody>
            {trades.map((trade) => (
              <tr key={trade._id} className="text-center border-t">
                <td className="py-2">{trade.offeredBy}</td>
                <td className="py-2">{trade.acceptedBy}</td>
                <td className="py-2">{trade.productOffered}</td>
                <td className="py-2">{trade.productGotten}</td>
                <td className="py-2">{new Date(trade.dateCreated).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TradeHistory;
