// frontend/src/pages/TradeInterface.js
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../App';

const TradeInterface = () => {
  const { user, socket, onlineUsers } = useContext(UserContext);
  const [listings, setListings] = useState([]);
  const [products, setProducts] = useState([]);
  const [selectedListing, setSelectedListing] = useState(null);
  const [selectedProduct, setSelectedProduct] = useState('');
  const [tradeRequests, setTradeRequests] = useState([]);

  const fetchListings = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/trade-listings');
      // Exclude user's own listings
      const otherListings = res.data.filter((l) => l.offeredBy !== user.username);
      setListings(otherListings);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      const userProducts = res.data.filter((p) => p.offeredBy === user.username);
      setProducts(userProducts);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchListings();
    fetchProducts();
  }, []);

  useEffect(() => {
    if (!socket) return;

    // Listen for incoming trade requests
    socket.on('receiveTradeRequest', (data) => {
      setTradeRequests((prev) => [...prev, data]);
    });

    // Listen for trade acceptance
    socket.on('tradeAccepted', (trade) => {
      alert(`Trade accepted by ${trade.acceptedBy}!`);
      fetchListings();
      fetchProducts();
    });

    // Listen for trade rejection
    socket.on('tradeRejected', (data) => {
      alert(`${data.to} has rejected your trade request.`);
    });

    // Cleanup listeners on unmount
    return () => {
      socket.off('receiveTradeRequest');
      socket.off('tradeAccepted');
      socket.off('tradeRejected');
    };
  }, [socket]);

  const handleTradeClick = (listing) => {
    setSelectedListing(listing);
  };

  const handleTradeRequest = async () => {
    if (!selectedProduct) {
      alert('Please select a product to offer');
      return;
    }

    try {
      // Create a trade request via the backend API
      const tradeRequestRes = await axios.post('http://localhost:5000/api/trades/request', {
        from: user.username,
        to: selectedListing.offeredBy,
        listingId: selectedListing._id,
        productOffered: selectedProduct,
      });

      const tradeRequest = tradeRequestRes.data;

      // Emit the trade request via Socket.io
      socket.emit('sendTradeRequest', {
        to: selectedListing.offeredBy,
        from: user.username,
        listingId: selectedListing._id,
        productOffered: selectedProduct,
        tradeRequestId: tradeRequest._id, // Include ID for tracking
      });

      alert('Trade request sent');
      setSelectedListing(null);
      setSelectedProduct('');
      fetchListings();
      fetchProducts();
    } catch (error) {
      alert(error.response?.data?.message || 'Error sending trade request');
    }
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Trade Interface</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {listings.map((listing) => {
          const isOnline = onlineUsers[listing.offeredBy];
          return (
            <div
                key={listing._id}
                className={`p-4 border rounded ${
              isOnline ? 'bg-green-100 cursor-pointer' : 'bg-gray-100 cursor-not-allowed'
                }`}
                onClick={() => isOnline && handleTradeClick(listing)}
                title={isOnline ? 'Click to trade' : 'User is offline'}
            >
              <h3 className="font-bold">{listing.product}</h3>
              <p>Price: ${listing.price}</p>
              <p>Shipping: {listing.shippingOptions}</p>
              <p>{listing.description}</p>
              <p className="text-sm text-gray-600">Offered by: {listing.offeredBy}</p>
              <span
                className={`inline-block mt-2 px-2 py-1 text-xs rounded ${
                  isOnline ? 'bg-green-500 text-white' : 'bg-gray-500 text-white'
                }`}
              >
                {isOnline ? 'Online' : 'Offline'}
              </span>
            </div>
          );
        })}
      </div>

      {selectedListing && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded w-full max-w-md">
            <h3 className="text-lg font-bold mb-4">Trade with {selectedListing.offeredBy}</h3>
            <p>Product Wanted: {selectedListing.product}</p>
            <div className="mt-4">
              <label className="block mb-2">Select Your Product to Offer:</label>
              <select
                value={selectedProduct}
                onChange={(e) => setSelectedProduct(e.target.value)}
                className="w-full px-3 py-2 border rounded"
              >
                <option value="">Select Product</option>
                {products.map((p) => (
                  <option key={p._id} value={p.name}>
                    {p.name}
                  </option>
                ))}
              </select>
            </div>
            <div className="mt-4 flex justify-end space-x-2">
              <button
                onClick={() => {
                  setSelectedListing(null);
                  setSelectedProduct('');
                }}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={handleTradeRequest}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Send Trade Request
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Trade Requests Modal */}
      {tradeRequests.length > 0 && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded w-full max-w-md">
            <h3 className="text-lg font-bold mb-4">Incoming Trade Requests</h3>
            {tradeRequests.map((request, index) => (
              <div key={index} className="mb-4 border-b pb-2">
                <p>
                  <strong>{request.from}</strong> wants to trade for your listing.
                </p>
                <div className="mt-2 flex space-x-2">
                  <button
                    onClick={async () => {
                      try {
                        // Accept the trade request via Socket.io
                        socket.emit('acceptTradeRequest', {
                          tradeRequestId: request.tradeRequestId,
                          acceptedBy: user.username,
                        });

                        // Remove the request from the state
                        setTradeRequests((prev) => prev.filter((_, i) => i !== index));
                      } catch (error) {
                        alert('Error accepting trade request');
                      }
                    }}
                    className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
                  >
                    Accept
                  </button>
                  <button
                    onClick={async () => {
                      try {
                        // Reject the trade request via Socket.io
                        socket.emit('rejectTradeRequest', {
                          tradeRequestId: request.tradeRequestId,
                          rejectedBy: user.username,
                        });

                        // Remove the request from the state
                        setTradeRequests((prev) => prev.filter((_, i) => i !== index));
                      } catch (error) {
                        alert('Error rejecting trade request');
                      }
                    }}
                    className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
                  >
                    Reject
                  </button>
                </div>
              </div>
            ))}
            <button
              onClick={() => setTradeRequests([])}
              className="mt-4 w-full bg-gray-300 text-black py-2 rounded hover:bg-gray-400"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TradeInterface;
